<?php// INCLUDED IN authentication.phpfunction initPDO(){		include('config.php');		$dbh = null;	try {	  $dbh = new PDO("mysql:host=$host_name; dbname=$database;", $user_name, $password);	} catch (PDOException $e) {	  echo "Error!: " . $e->getMessage() . "<br/>";	  die();	}	return $dbh;}	function db($task, $variables) {		$dbh = initPDO();		switch($task) {		case "checkPassword":			$statement = $dbh->prepare("SELECT password FROM users WHERE username = :username OR email = :username LIMIT 1");			$statement->execute($variables);			$password = $statement->fetch();			return $password;			break;		case "checkUserDoesNotExist":			$statement = $dbh->prepare("SELECT user_id FROM users WHERE username = :username OR email = :email");			$statement->execute($variables);			$user = $statement->fetch();			return $user;			break;		case "createUser":			$statement = $dbh->prepare("INSERT INTO users VALUES (NULL, :username, :email, :password, :created)");			$statement->execute($variables);			break;		default:			echo "Task '$task' not found";	}}?>